    <script>
        // Lấy tất cả các phần tử a trong menu
        const menuItems = document.querySelectorAll('.mcd-menu li a');

        // Thêm sự kiện mouseover và mouseout cho từng phần tử a
        menuItems.forEach(item => {
            item.addEventListener('mouseover', function() {
                // Xóa class active từ tất cả các phần tử a
                menuItems.forEach(i => i.classList.remove('active'));
                // Thêm class active cho phần tử a hiện tại
                this.classList.add('active');
            });

            item.addEventListener('mouseout', function() {
                // Xóa class active khi chuột ra khỏi phần tử a
                this.classList.remove('active');
            });
        });
    </script>
